"use strict";angular.module("minecraftScaleCalculator",[]).controller("tswMcSCalculator",[function(){var a=this;a.data={distance:1e3,unit:"mm",scale:2},a.blocks=function(){var t;switch(a.data.unit){case"mm":t=a.data.distance/1e3;break;case"cm":t=a.data.distance/100;break;case"m":t=a.data.distance;break;case"inches":t=a.data.distance/39.37;break;case"feet":t=a.data.distance/3.2808;break;default:t=a.data.distance/1e3}var e=Math.round(t).toFixed(1);return(e*a.data.scale).toFixed(0)}}]);