describe("minecraftScaleCalculator",function(){beforeEach(module("minecraftScaleCalculator"));var t;beforeEach(inject(function(e){t=e})),describe("tswMcSCalculator",function(){beforeEach(function(){controller=t("TswMcSCalculator")}),it("should be defined",inject(function(t){expect(controller).toBeDefined()})),it("has default values for distance, unit, scale",function(t){expect(controller.data.distance).toEqual(1e3),expect(controller.data.unit).toEqual("mm"),expect(controller.data.scale).toEqual(2)}),it("calculates default value to correct number of blocks",function(t){expect(controller.blocks()).toEqual("2")}),it("calculates default values in cm to correct number of blocks",function(t){controller.data.unit="cm",expect(controller.blocks()).toEqual("20")}),it("calculates default values in m to correct number of blocks",function(t){controller.data.unit="m",expect(controller.blocks()).toEqual("2000")}),it("calculates default values in feet to correct number of blocks",function(t){controller.data.unit="feet",expect(controller.blocks()).toEqual("610")}),it("calculates default values in inches to correct number of blocks",function(t){controller.data.unit="inches",expect(controller.blocks()).toEqual("50")}),it("calculates default values in mm with a scale of 8 to correct number of blocks",function(t){controller.data.scale="8",expect(controller.blocks()).toEqual("8")}),it("calculates default values in inches with a scale of 8 to correct number of blocks",function(t){controller.data.scale="8",controller.data.unit="inches",expect(controller.blocks()).toEqual("200")})})});